import { StyleSheet, Text, View, SafeAreaView, ScrollView} from 'react-native'
import React, {useEffect, useState} from 'react'
import { Divider } from 'react-native-elements'
import HeaderTabs from '../components/HeaderTabs'
import SearchBar from '../components/SearchBar'
import Ionicons from 'react-native-vector-icons/Ionicons';
import Categories from '../components/Categories';
import RestaurentItems, { localRestaurants } from '../components/RestaurentItems';
import BottomTabs from '../components/BottomTabs'

const YELP_API_KEY = "_Iw7HUAmJ-V6_MHZiI5qaZQzigHSRvCyXRzZj-le8P9bC055P5ldjxIUuw_crAfsVVWaqRj2kRN6_677DutRMcH1odBVzUE1z8RVFf0SMG3r5XpytlEoj1Xpe8aQYnYx";

const Home = () => {
  const [restaurentData, setRestaurentData] = useState(localRestaurants);
  const [city, setCity] = useState('San Francisco')
  const [activeTab, setActiveTab] = useState('Delivery')
  
  const getRestaurantsFromYelp = () => {
    const yelpUrl = `https://api.yelp.com/v3/businesses/search?term=restaurants&location=${city}`;
    // const yelpUrl = 'https://cors-anywhere.herokuapp.com/https://api.yelp.com/v3/businesses/search?term=restaurants&location=Hollywood';
    
    const apiOptions = {
      headers: {
        Authorization: `Bearer ${YELP_API_KEY}`,
      }
    }
    
    return fetch(yelpUrl, apiOptions)
      .then((res) => res.json())
      .then((json) => 
        setRestaurentData(
          json.businesses.filter((business) => 
            business.transactions.includes(activeTab.toLowerCase())
          )
        )
      )
  }
  
  // useEffect(() => {
  //   getRestaurantsFromYelp();
  // }, [city, activeTab])

  return (
    <SafeAreaView style={{backgroundColor:'#white', flex:1}}>
      {/* <Text>Home</Text> */}
      <View style={{backgroundColor:'#eee', padding: 15}}>
         <HeaderTabs activeTab={activeTab} setActiveTab={setActiveTab}/>
         <SearchBar cityHandler={setCity}/>
      </View>
      {/* use stickyHeaderIndices={[1]}  */}
      <ScrollView showsVerticalScrollIndicator={false}>
        <Categories/>
        <RestaurentItems restaurentData={restaurentData}/>
      </ScrollView>
      <Divider width={1}/>\
      <BottomTabs/>
    </SafeAreaView>
  )
}

export default Home

const styles = StyleSheet.create({})
