import { StyleSheet, Text, View, TouchableOpacity } from 'react-native'
import React, {useState} from 'react'

const HeaderTabs = () => {
  const [activeTab, setActiveTab] = useState('Delivery')

  return (
    <View style={{flexDirection:'row', alignSelf:'center'}}>
      <HeaderButton text="Delivery" btnColor='black' textColor='#eee' activeTab={activeTab} setActiveTab={setActiveTab}/>
      <HeaderButton text="Pickup" btnColor='#eee' textColor='black' activeTab={activeTab} setActiveTab={setActiveTab}/>
    </View>
  )
}

const HeaderButton = (props) => {
    return (
        <TouchableOpacity style={{
            backgroundColor: props.activeTab === props.text ? 'black' : '#eee',
            paddingVertical: 6,
            paddingHorizontal: 16,
            borderRadius: 30,
        }}
        onPress = {() => props.setActiveTab(props.text)}
        >
            <Text style={{color: props.activeTab === props.text ? '#eee' : 'black', fontSize: 15}}>
                {props.text}
            </Text>
        </TouchableOpacity>
    )
}

export default HeaderTabs

const styles = StyleSheet.create({})
