import { View, Text, Image, TouchableOpacity} from 'react-native'
import React from 'react'
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons';

export default function RestaurentItem() {
  return (
    <TouchableOpacity activeOpacity={1} style={{marginBottom: 30 }}>
        <View style={{marginTop: 10, padding: 15}}>
            <RestaurentImage/>
            <RestaurentInfo/>
        </View>
    </TouchableOpacity>  
  )
}

const RestaurentImage = () => (
    <>
    <Image
        source={{
            uri: 'https://img.traveltriangle.com/blog/wp-content/uploads/2017/10/Toit1.jpg'
        }}
        style={{width: '100%', height: 200}}
    />
    <TouchableOpacity style={{position: 'absolute', top: 18, right: 20}}>
        <MaterialCommunityIcons name='heart-outline' size={25} color='#fff'/>
    </TouchableOpacity>
    </>
);

const RestaurentInfo = () => (
    <View style={{
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginTop: 10,
    }}>
     <View>
         <Text style={{fontSize: 15, fontWeight:'bold'}}>
             Farmhouse Kitchen Thai Cuisine
         </Text>
         <Text style={{fontSize: 13, color:'gray'}}>
             30-45 * mins
         </Text>
     </View>

     <View style={{
         backgroundColor: '#eee', 
         height: 30, 
         width: 30, 
         alignItems:'center',
         borderRadius: 15
     }}>
         <Text style={{paddingTop: 4}}>4.5</Text>
     </View>
    </View>
);
