import { View, Text, StyleSheet, Image } from 'react-native'
import React from 'react'

const foods = [
    {
        title: 'Lasagna',
        description: "With butter lettuce, tomato and sauce",
        price: "$14.50",
        image: "https://www.southsidepizzahk.com/wp-content/uploads/2021/03/Lasagne.jpeg"
    },
    {
        title: 'Tandoori Chicken',
        description: "Amazing Indian Dish as the name suggests",
        price: "$24.50",
        image: "https://www.spiceindiaonline.com/wp-content/uploads/2021/05/Tandoori-Chicken-20.jpg"
    },
    {
        title: 'Indian Food Trail',
        description: "A plate that makes you whale",
        price: "$44.50",
        image: "https://d4t7t8y8xqo0t.cloudfront.net/resized/750X436/eazytrendz%2F3070%2Ftrend20210218124824.jpg"
    },
]

export default function MenuItems () {
  return (
    <View style={styles.menuItemStyle}>
      <FoodInfo food = {foods[0]} />
      <FoodImage food = {foods[0]} />
    </View>
  )
}

const FoodInfo = (props) => (
    <View style={{width: 240, justifyContent: "space-evenly"}}>
        <Text style={styles.titleStyle}>{props.food.title}</Text>
        <Text>{props.food.description}</Text>
        <Text>{props.food.price}</Text>
    </View>
)

const FoodImage = (props) => (
    <View style={{width: 240, justifyContent: "space-evenly"}}>
        <Image 
            source={{uri: props.food.image}} 
            style={{width: 100, height: 100, borderRadius: 8}}
        />
    </View>
)

const styles = StyleSheet.create({
    menuItemStyle:  {
        flexDirection: 'row',
        justifyContent: 'space-between',
        margin: 20,
    },
    titleStyle: {
        fontSize: 19,
        fontWeight: "600"
    }
})
