import { View, Text, Image } from 'react-native'
import React from 'react'

const yelpRestaurantInfo = {
    name: "Farmhouse Kitchen Thai Cuisine",
    image: "https://static.onecms.io/wp-content/uploads/sites/9/2020/04/24/ppp-why-wont-anyone-rescue-restaurants-FT-BLOG0420.jpg",
    price: "$$",
    reviews: "1500",
    rating: 4.5,
    categories: [{ title: 'Thai' }, { title: "Comfort Food" }],
};

// const { name, image, price, reviews, rating, categories } = yelpRestaurantInfo;

// const image = "https://static.onecms.io/wp-content/uploads/sites/9/2020/04/24/ppp-why-wont-anyone-rescue-restaurants-FT-BLOG0420.jpg"
// const title = 'Farmhouse Kitchen Thai Cuisine'
// const description = 'Thai ‚Ä¢ Comfort Food ‚Ä¢ $$ ‚Ä¢ üé´ ‚Ä¢ 4 ‚≠ê (2913+)';


export default function About(props) {
    const { name, image, price, reviews, rating, categories } = props.route.params;

    const formattedCategories = categories.map((cat) => cat.title).join(' ‚Ä¢ ');

    const description = `${formattedCategories} ${price ? " ‚Ä¢ " + price : ' '} ‚Ä¢ üé´ ‚Ä¢ ${rating} ‚≠ê (${reviews}+)`;

    return (
        <View>
            <RestaurantImage image={image} />
            <RestaurantName name={name} />
            <RestaurantDescription description={description} />
        </View>
    )
}

const RestaurantImage = (props) => (
    <Image source={{ uri: props.image }} style={{ width: "100%", height: 180 }} />
);

const RestaurantName = (props) => (
    <Text style={{
        fontSize: 20,
        fontWeight: '600',
        marginTop: 10,
        marginHorizontal: 15,
    }}>
        {props.name}
    </Text>
);

const RestaurantDescription = (props) => (
    <Text style={{
        fontSize: 15.5,
        fontWeight: '400',
        marginTop: 10,
        marginHorizontal: 15,
    }}>
        {props.description}
    </Text>
);
