import { View, Text } from 'react-native'
import React from 'react'
import About from '../components/restaurantDetail/About'
import { Divider } from 'react-native-elements'
import MenuItems from '../components/restaurantDetail/MenuItems'
import ViewCart from '../components/restaurantDetail/ViewCart'

const foods = [
  {
      title: 'Lasagna',
      description: "With butter lettuce, tomato and sauce",
      price: "$14.50",
      image: "https://www.southsidepizzahk.com/wp-content/uploads/2021/03/Lasagne.jpeg"
  },
  {
      title: 'Tandoori Chicken',
      description: "Amazing Indian Dish as the name suggests",
      price: "$24.50",
      image: "https://www.spiceindiaonline.com/wp-content/uploads/2021/05/Tandoori-Chicken-20.jpg"
  },
  {
      title: 'Indian Food Trail',
      description: "A plate that makes you whale",
      price: "$44.50",
      image: "https://d4t7t8y8xqo0t.cloudfront.net/resized/750X436/eazytrendz%2F3070%2Ftrend20210218124824.jpg"
  },
]

export default function RestaurantDetails({route, navigation}) {
  return (
      <View>
        <About route={route}/>
        <Divider width={1.8} style={{marginVertical: 20}} />
        <MenuItems restaurantName = {route.params.name} foods={foods}/>
        <ViewCart navigation={navigation} restaurantName = {route.params.name} />
    </View>
  )
}
