import { View, Text, Image, TouchableOpacity} from 'react-native'
import React from 'react'
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons';

export const localRestaurants = [
    {
      name: "Beachside Bar",
      image_url:
        "https://static.onecms.io/wp-content/uploads/sites/9/2020/04/24/ppp-why-wont-anyone-rescue-restaurants-FT-BLOG0420.jpg",
      categories: ["Cafe", "Bar"],
      price: "$$",
      reviews: 1244,
      rating: 4.5,
    },
    {
      name: "Benihana",
      image_url:
        "https://storage.googleapis.com/benihana-2020-238473673204-wp-data/wp-media/irving_texas.jpg",
      categories: ["Cafe", "Bar"],
      price: "$$",
      reviews: 1244,
      rating: 3.7,
    },
    {
      name: "India's Grill",
      image_url:
        "https://restaurantindia.s3.ap-south-1.amazonaws.com/s3fs-public/news8812.jpg",
      categories: ["Indian", "Bar"],
      price: "$$",
      reviews: 700,
      rating: 4.9,
    },
  ];

export default function RestaurentItems() {
  return (
    <TouchableOpacity activeOpacity={1} style={{marginBottom: 30 }}>
        {localRestaurants.map((restaurent, index) => (
        <View key={index} style={{marginTop: 5, padding: 15, backgroundColor:'#fff'}}>
            <RestaurentImage image={restaurent.image_url}/>
            <RestaurentInfo
                name = {restaurent.name}
                rating = {restaurent.rating}
            />
        </View>
        ))}
    </TouchableOpacity>  
  )
}

const RestaurentImage = (props) => (
    <>
    <Image
        source={{
            // uri: 'https://img.traveltriangle.com/blog/wp-content/uploads/2017/10/Toit1.jpg'
            uri: props.image
        }}
        style={{width: '100%', height: 200}}
    />
    <TouchableOpacity style={{position: 'absolute', top: 18, right: 20}}>
        <MaterialCommunityIcons name='heart-outline' size={25} color='#fff'/>
    </TouchableOpacity>
    </>
);

const RestaurentInfo = (props) => (
    <View style={{
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginTop: 10,
    }}>
     <View>
         <Text style={{fontSize: 15, fontWeight:'bold'}}>
             {/* Farmhouse Kitchen Thai Cuisine */}
             {props.name}
         </Text>
         <Text style={{fontSize: 13, color:'gray'}}>
             30-45 * mins
         </Text>
     </View>

     <View style={{
         backgroundColor: '#eee', 
         height: 30, 
         width: 30, 
         alignItems:'center',
         borderRadius: 15
     }}>
         <Text style={{paddingTop: 4}}>{props.rating}</Text>
     </View>
    </View>
);
